import React from "react";
import TextField from "@mui/material/TextField";
import BoldText from "./BoldText";

interface CreateTestCasesProps {
  testCase: string;
  setTestCase: (value: string) => void;
  generateTestCases: () => void;
  generateTestData: () => void;
}

const CreateTestCases: React.FC<CreateTestCasesProps> = ({
  testCase,
  setTestCase,
  generateTestCases,
  generateTestData,
}) => {
  const [edit, setEdit] = React.useState(false);
  return (
    <div>
      <div style={{ display: "flex", justifyContent: "space-between" }}>
        <h2>Test Cases</h2>
        <button
          className="newConversationButton"
          style={{ width: "100px", height: 30 }}
          onClick={() => setEdit(!edit)}
        >
          {edit ? "< Back" : "Edit"}
          <img
            src="data:image/png;base64,"
            alt="Clear Chat"
          />
        </button>
      </div>
      {edit ? (
        <TextField
          value={testCase}
          onChange={(e) => {
            setTestCase(e.target.value);
            localStorage.setItem("testCase", e.target.value);
          }}
          variant="outlined"
          multiline
          fullWidth
        />
      ) : (
        <>
          <div className="chat-msg-list msg-hldr-cb gap10px pre-div ">
            <BoldText text={testCase} />
          </div>
          <br />

          <div style={{ display: "flex", justifyContent: "flex-end" }}>
            <button
              className="newConversationButton"
              style={{ width: "100px" }}
              onClick={() => generateTestCases()}
            >
              Re-generate
              <img
                src="data:image/png;base64,"
                alt="Clear Chat"
              />
            </button>
            <button
              className="newConversationButton"
              style={{ width: "130px" }}
              onClick={() => generateTestData()}
            >
              Generate test data
              <img
                src="data:image/png;base64,"
                alt="Clear Chat"
              />
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default CreateTestCases;
